const initialState = {
  list: [],
  pageNumber: 1,
  searchQuery: "",
};

const listReducer = (state = initialState, action) => {
  switch (action.type) {
    // add new data at the end list
    case "APPEND": {
      const newData = action.payload.map((item) => {
        item.show = true; // adding one more property on basis we can show element
        return item;
      });
      return {
        list: [...state.list, ...newData],
        pageNumber: state.pageNumber + 1,
      };
    }

    // search on basis of text
    case "SEARCH": {
      const searchList = state.list.filter((item) => item.title.includes(state.searchQuery));
      console.log(searchList);
      return {
        list: searchList,
      };
    }

    // sort my members
    case "SORT": {
      const sortedOnMembers = state.list;
      sortedOnMembers.sort((a, b) => (a.members > b.members ? -1 : 1));
      return {
        list: sortedOnMembers,
      };
    }

    // reset sorting and seach filter
    case "RESETFILTER": {
      const defaultSort = state.list;
      defaultSort.sort((a, b) => (a.score > b.score ? -1 : 1));
      return {
        list: defaultSort,
      };
    }

    default:
      return state;
  }
};

export default listReducer;
