const initialState = {
    list: [],
    pageNumber:1
}

const listReducer = (state = initialState, action) => {

    switch (action.type) {
        case "APPEND" :
            const items = getNewItems();
            state.list = [...state.list]
    }

  state.list = [
    {
      id: 1,
      name: "Manish",
      age: "23",
    },
    {
      id: 2,
      name: "Abc",
      age: "23",
    },
    {
      id: 3,
      name: "Xyz",
      age: "23",
    },
    {
      id: 4,
      name: "Mandela",
      age: "23",
    },
    {
      id: 5,
      name: "Vipin",
      age: "23",
    },
  ];

  return state;
};

export default listReducer;
