  pageNumber: 1,
};

const getNewItems = (pageNumber) => {
    let items;
    const url = `https://api.jikan.moe/v3/top/anime/${pageNumber}/airing`;
    fetch(url)
    .then(res => res.json())
    .then(obj => items = obj.top)
    console.log(items)
    return items;
};

const listReducer = (state = initialState, action) => {
  switch (action.type) {
    case "APPEND":
      const items = getNewItems(state.pageNumber);
      return { list: [...state.list], pageNumber: state.pageNumber + 1 };
  }

