import { View, Text, FlatList } from "react-native";
import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import ProductItem from "./ProductItem";

export default function ProductList(props) {
  const renderItem = ({ item }) => <ProductItem item={item}></ProductItem>;

  let list = useSelector((state) => state.list);
  let pageNum = useSelector((state) => state.pageNumber);


  const dispatch = useDispatch();
  useEffect(() => {
    console.log("Involing use effect");
    fetchNewItems();
  }, []);

  const fetchNewItems = () => {
    fetch(`https://api.jikan.moe/v3/top/anime/${pageNum}/airing`)
      .then((res) => res.json())
      .then((obj) => {
        dispatch({ type: "APPEND", payload: obj.top });
      });
  };

  return (
    <View>
      <FlatList
        data={list}
        renderItem={renderItem}
        keyExtractor={(item) => {
          "" + item.rank;
        }}
      />
    </View>
  );
}
