      type: "TV",
      episodes: 12,
      start_date: "Jan 2021",
      end_date: {},
      members: 250498,
      score: 8.73,
    },
  ],
  pageNumber: 1,
};

const listReducer = (state = initialState, action) => {
  switch (action.type) {
    case "APPEND":
      fetch(`https://api.jikan.moe/v3/top/anime/${state.pageNumber}/airing`)
        .then((res) => res.json())
        .then((obj) => {

          return {
            list: [...state.list, ...obj.top],
            pageNumber: state.pageNumber + 1,
          };
        });
  }

