const initialState = {
  list: [],
  pageNumber: 1,
};

const getNewItems = (pageNumber) => {
    let items;
    const url = `https://api.jikan.moe/v3/top/anime/${pageNumber}/airing`;
    fetch(res => res.json())
    .then()
};

const listReducer = (state = initialState, action) => {
  switch (action.type) {
    case "APPEND":
      const items = getNewItems(state.pageNumber);
      return { list: [...state.list, ...items], pageNumber: pageNumber + 1 };
  }

  state.list = [
    {
      id: 1,
      name: "Manish",
      age: "23",
    },
    {
      id: 2,
      name: "Abc",
      age: "23",
    },
    {
      id: 3,
      name: "Xyz",
      age: "23",
    },
    {
      id: 4,
      name: "Mandela",
      age: "23",
    },
    {
      id: 5,
      name: "Vipin",
      age: "23",
    },
  ];

  return state;
};

export default listReducer;
