const initialState = {
  list: [],
  pageNumber: 1,
};

const getNewItems = (pageNumber) => {
    let items;

    console.log(items);
    return items;
};

const listReducer = (state = initialState, action) => {
  switch (action.type) {
    case "APPEND":
      const items = getNewItems(state.pageNumber);
      return { list: [...state.list, ...items], pageNumber: state.pageNumber + 1 };
  }

