import { NavigationContainer } from "@react-navigation/native";
import React, { useState } from "react";
import {FlatList,Button,Dimensions,ImageBackground, Image, SafeAreaView, StatusBar, StyleSheet, Text, TouchableOpacity, View } from "react-native";

class App extends React.Component {
  constructor() {
    super();
    this.state={
      data:[]
    }
  }
  // API fetch
  componentDidMount() {
    this.apiCall();
  }
  async apiCall() {
    let resp = await fetch("https://corona.lmao.ninja/v2/countries")
    let respJson = await resp.json()
    console.warn("data", respJson)
    this.setState({data : respJson})
  }


  render()  {
    const renderItem = ({ item }) => {
      return (
        // <View>
        //   <Image style={{width:100,height:100}}
        //     source = {{uri : item.countryInfo.flag}}
        //   />
        //   <View>
        //     <Text>
        //       {item.country}
        //     </Text>
        //     <Text>
        //       {item.cases}
        //     </Text>
        //   </View>
        // </View>
        <View style={{flex:1, justifyContent:'center',alignItems:"center",margin:20}}>
            <View style={{width:Dimensions.get('window').width *0.0 ,height:Dimensions.get('window').height*0.2}}>
           <ImageBackground source={{uri:item.countryInfo.flag}} style={{width:'50%',height:'100%',justifyContent:"flex-end"}}>
           <View style={{flex:0.3,flexDirection:"row",height:"20%",justifyContent:"space-between",alignItems:"center",backgroundColor:"grey",borderBottomLeftRadius:10,borderBottomRightRadius:10,padding:10}}>
                <Text style={{flex:0.4,alignItems:"center",textAlign:"center"}}>{item.country} </Text>
                <Text style={{flex:0.25}}>{item.cases} </Text>
                <Button title="View Details" onPress={()=>{this.props.navigateProduct(item.country)}}  style={{padding:5}}></Button>
            </View>
           </ImageBackground>        
            </View>
        </View>
      )
    }

    return (
      <SafeAreaView style={styles.container}>
        <FlatList
          data={this.state.data}
          renderItem={renderItem}
        />
        </SafeAreaView>
    )
  }
}


const styles=StyleSheet.create({
  card:{backgroundColor: "grey",
  borderWidth:5,
  borderRadius:4,
  margin:10,
  padding:10,
  width:80,
  height:80
  }
});


export default App
