import React from 'react';
import {View, TextInput, StyleSheet,Button} from 'react-native';
import { useEffect } from 'react/cjs/react.development';


function FitlterCountry(props){
    const updateNewResult=()=>{
        searchFilter('');
    }
    const [value, setValue] = React.useState('');
    const {searchFilter} = props;
    return (
        <View >
            <View  style={styles.container}>
            <TextInput 
                style={styles.TextArea}  
                value={value} 
                onChangeText={(value) => {
                    setValue(value);
                    searchFilter(value)} 
                }></TextInput>
            <Button color='black' title ="Sort" onPress={()=>{props.ownFilter();}}></Button>
            <Button color='black' title="X" onPress={()=>{setValue(" ");updateNewResult()}}></Button>
            </View>
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        flexDirection:'row',
        justifyContent:'center',
        alignItems:'center',
        margin:20,
        justifyContent:'space-around',
     },
    TextArea: {
        placeholder:"Type Country Name...",
        width:'200%',
        backgroundColor:'white',
        height:40,
        color:'black',
        padding:10,
        borderRadius:10 
    }
});


export default FitlterCountry;
