import React, { useEffect, useState } from "react";
import {FlatList,Button,Dimensions,ImageBackground, Image, SafeAreaView, StatusBar, StyleSheet, Text, TouchableOpacity, View } from "react-native";
import FitlterCountry from "./FilterCountry"

const Main = () => {
  const [Countries,setCountries] = useState([]);
  const [filteredCountries,setFilteredCountries] = useState("");
  const [filterType,setFilteredType] = useState('');


  useEffect(() => {
      fetch("https://corona.lmao.ninja/v2/countries")
      .then((res) => res.json())
      .then((json)=>setCountries(json))
      .catch((error) => console.error(error))
  }, [])

  const onSearchFilter = useCallback( (value) =>{
    setFilteredCountries(value);
  });

  const onDateFilter=useCallback(()=>{
    setFilterType(filterType=='price'?'rating':'price');
  })

  return(
    <View style={styles.container}>
      <FitlterCountry searchFilter={onSearchFilter}  dateFilter={onDateFilter}></FitlterCountry>
    </View>
  )

}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingTop:15,
    backgroundColor: 'black',
    alignItems: 'center',
    justifyContent: 'center',
  },
});

export default Main;





























