import React, { useState } from "react";
import { FlatList, Image, SafeAreaView, StatusBar, StyleSheet, Text, TouchableOpacity, View } from "react-native";

class App extends React.Component {
  constructor() {
    super();
    this.state={
      data:[]
    }
  }
  // API fetch
  componentDidMount() {
    this.apiCall();
  }
  async apiCall() {
    let resp = await fetch("https://corona.lmao.ninja/v2/countries")
    let respJson = await resp.json()
    console.warn("data", respJson)
    this.setState({data : respJson})
  }


  render()  {



    const renderItem = ({ item ,index}) => {
      let{cardText,card,cardImage} = styles
      return(
        <TouchableOpacity style={card}>
          <image style={cardImage} source={{uri:"https://disease.sh/assets/img/flags/af.png"}}/>.
          <Text style={cardText}>{item.country}</Text>
        </TouchableOpacity>
      )

      // return (
      //   <View>
      //     <Image style={{width:100,height:100}}
      //       source = {{uri : item.countryInfo.flag}}
      //     />
      //     <View>
      //       <Text>
      //         {item.country}
      //       </Text>
      //       <Text>
      //         {item.cases}
      //       </Text>
      //     </View>
      //   </View>
      // )
    }

    return (
      <SafeAreaView style={styles.container}>
        <FlatList
          data={this.state.data}
          renderItem={renderItem}
        />
        </SafeAreaView>
    )
  }
}


const styles = StyleSheet.create({
  container: {
  },
  item: {

  },
  title: {
  },
});


export default App
