import React, { useEffect, useState ,useCallback} from "react";
import {FlatList,Button,Dimensions,ImageBackground, Image, SafeAreaView, StatusBar, StyleSheet, Text, TouchableOpacity, View } from "react-native";
import FitlterCountry from "./FilterCountry"

const Main = () => {
  const [Countries,setCountries] = useState([]);
  const [filteredCountries,setFilteredCountries] = useState("");
  const [filterType,setFilteredType] = useState('');


  useEffect(() => {
    async function fetchMyAPI() {
      let response = await fetch('https://corona.lmao.ninja/v2/countries')
      response = await response.json()
      setCountries(response)
      // console.log(Countries[0])
    }
    fetchMyAPI()
  }, [])

  const onSearchFilter = useCallback( (value) =>{
    setFilteredCountries(value);
    // console.warn(filteredCountries)
  });

  const onDateFilter=useCallback(()=>{
    setFilterType(filterType=='price'?'rating':'price');
  })

  return(
    <View style={styles.container}>
      <FitlterCountry data={Countries} searchFilter={onSearchFilter}  dateFilter={onDateFilter}></FitlterCountry>
    </View>
  )

}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingTop:15,
    backgroundColor: 'blue',
    alignItems: 'center',
    justifyContent: 'center',
  },
});

export default Main;





























