                  <Text style={{fontWeight:"bold",fontSize:"25px",color:"white",flex:0.25}}>{item.cases} </Text>
                  <Button title="View Details" onPress={()=>{this.props.navigateProduct(item.country)}}  style={{padding:5}}></Button>
