import {View, TextInput, Button} from 'react-native';
import { useEffect } from 'react/cjs/react.development';


function FitlterCountry(props){
    const updateNewResult=()=>{
        searchFilter('');
    }
    const [value, setValue] = React.useState('');
    const {searchFilter} = props;
    return (
        <View >
            <View  style={styles.container}>
            <TextInput onChangeText={(value) =>{setValue(value);searchFilter(value)} } value={value} placeholder="Type Country Name..." style={{width:'200%',backgroundColor:'white',height:40,color:'black',padding:10,borderRadius:10}} ></TextInput>
            <Button color='black' title ="Sort" onPress={()=>{props.dateFilter();}}></Button>
            <Button color='black' title="X" onPress={()=>{setValue(" ");updateNewResult()}}></Button>
            </View>
        </View>
    );
}

const styles = StyleSheet.create({
    container: {

            flexDirection:'row',
            justifyContent:'center',
            alignItems:'center',
            margin:20,
            justifyContent:'space-around',
  }
});


export default FitlterCountry;
