import React, { useState } from "react";
import { FlatList, SafeAreaView, StatusBar, StyleSheet, Text, TouchableOpacity, View } from "react-native";


const Item = ({ title }) => (
  <View style={styles.item}>
    <Text style={styles.title}>{title}</Text>
  </View>
);


class App extends React.Component {
  constructor() {
    super();
    this.state={
      data:[]
    }
  }
  componentDidMount() {
    this.apiCall();
  }
  async apiCall() {
    let resp = await fetch("https://corona.lmao.ninja/v2/countries")
    let respJson = await resp.json()
    console.warn("data", respJson)
    this.setState({data : respJson})
  }


  render()  {
    let flag = item.countryInfo.flag      //getting flag url

    const renderItem = ({ item }) => (
      <Image source={require(flag)} />
    );

    return (
      <SafeAreaView style={styles.container}>
        <FlatList
          data={this.state.data}
          renderItem={renderItem}

          // renderItem={({item}) => <Text>{item.country}{item.cases}</Text>}
        />
        </SafeAreaView>
    )
  }
}
const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginTop: StatusBar.currentHeight || 0,
  },
  item: {
    backgroundColor: '#f9c2ff',
    padding: 20,
    marginVertical: 8,
    marginHorizontal: 16,
  },
  title: {
    fontSize: 32,
  },
});


export default App
